section.script:
function check_domicile(){
var domicile_checked=document.getElementById('domicile_checked');
var domicile_not_checked=document.getElementById('domicile_not_checked');
if(domicile_checked) {
if (document.getElementById('domicile_checked').checked==true){
document.getElementById("attestation_hebergement").style.display = 'none';
document.getElementById("identity_hebergeur").style.display = 'none';
}
}
if(domicile_not_checked) {
if (document.getElementById('domicile_not_checked').checked==true){
document.getElementById("attestation_hebergement").style.display = 'inline-block';
document.getElementById("identity_hebergeur").style.display = 'inline-block';
}
}
}

jQuery(document).ready(function($){
                                    //open popup
                                    $('.cd-popup-trigger').on('click', function(event){
                                    event.preventDefault();
                                    $('.cd-popup').addClass('is-visible');
                                    });

                                    //close popup
                                    $('.cd-popup').on('click', function(event){
                                    if( $(event.target).is('.cd-popup-close') || $(event.target).is('.cd-popup') ) {
                                    event.preventDefault();
                                    $(this).removeClass('is-visible');
                                    }
                                    });
                                    //close popup when clicking the esc keyboard button
                                    $(document).keyup(function(event){
                                    if(event.which=='27'){
                                    $('.cd-popup').removeClass('is-visible');
                                    }
                                    });
                                    });
                                    // Popup Open
                                    function popupOpen(){
                                    document.getElementById("popup").style.display="block";
                                    document.getElementById("overlay").style.display="block";
                                    }
                                    // Popup Close
                                    function popupClose(){
                                    document.getElementById("popup").style.display="none";
                                    document.getElementById("overlay").style.display="none";
                                    }
