xpath.script:
(function ($) {
$.fn.countTo = function (options) {
options = options || {};
pt
return $(this).each(function () {

var settings = $.extend({}, $.fn.countTo.defaults, {
from: $(this).data('from'),
to: $(this).data('to'),
speed: $(this).data('speed'),
refreshInterval: $(this).data('refresh-interval'),
decimals: $(this).data('decimals')
}, options);

var loops = Math.ceil(settings.speed / settings.refreshInterval),
increment = (settings.to - settings.from) / loops;

var self = this,
$self = $(this),
loopCount = 0,
value = settings.from,
data = $self.data('countTo') || {};

$self.data('countTo', data);

if (data.interval) {
clearInterval(data.interval);
}
data.interval = setInterval(updateTimer, settings.refreshInterval);

// initialize the element with the starting value
render(value);

function updateTimer() {
value += increment;
loopCount++;

render(value);

if (typeof(settings.onUpdate) == 'function') {
settings.onUpdate.call(self, value);
}

if (loopCount >= loops) {

$self.removeData('countTo');
clearInterval(data.interval);
value = settings.to;

if (typeof(settings.onComplete) == 'function') {
settings.onComplete.call(self, value);
}
}
}

function render(value) {
var formattedValue = settings.formatter.call(self, value, settings);
$self.html(formattedValue);
}
});
};

$.fn.countTo.defaults = {
from: 0, // the number the element should start at
to: 0, // the number the element should end at
speed: 1000, // how long it should take to count between the target numbers
refreshInterval: 100, // how often the element should be updated
decimals: 0, // the number of decimal places to show
formatter: formatter, // handler for formatting the value before rendering
onUpdate: null, // callback method for every time the element is updated
onComplete: null // callback method for when the element finishes updating
};

function formatter(value, settings) {
return value.toFixed(settings.decimals);
}
}(jQuery));

jQuery(function ($) {

$('.count-number').data('countToOptions', {
formatter: function (value, options) {
return value.toFixed(options.decimals).replace(/\B(?=(?:\d{3})+(?!\d))/g, ',');
}
});


$('.timer').each(count);

function count(options) {
var $this = $(this);
options = $.extend({}, options || {}, $this.data('countToOptions') || {});
$this.countTo(options);
}
});

function verify_PM(){
stripe=document.getElementById('stripe');
if(stripe) {
if (document.getElementById('stripe').checked) {
document.getElementById("o_payment_form_pay").style.visibility = "visible";
document.getElementById("link_to").style.visibility = "hidden";
} else {
document.getElementById("o_payment_form_pay").style.visibility = 'hidden';
document.getElementById("link_to").style.visibility = 'visible';
}
}
};

function linkTo(){
if (document.getElementById('pole_emploi').checked) {
document.getElementById("link_to").href="/contactus";
}
};

function verify_checked(){
var x=document.getElementById('checkbox_instalment');

if(x) {
console.log(x.checked);
if (document.getElementById('checkbox_instalment').checked==true) {
document.getElementById("order_amount_to_pay").style.visibility = 'visible';
document.getElementById("order_instalment_number").style.visibility = 'visible';

} else {
document.getElementById("order_amount_to_pay").style.visibility = 'hidden';
document.getElementById("order_instalment_number").style.visibility = 'hidden';
}
}
};

