section.script:
$( document ).ready(function() {
	$( "#sh_corpomate_theme_section_55 .sh_progress" ).each(function( index ) {
		var sh_progress_span = $( this ).find('.sh_right_pers');
		if(sh_progress_span.length){
		$( this ).find('.progress-bar').css("width",sh_progress_span.text() );
		}
	});	

});


$( document ).ready(function() {
